{
    "swagger": "2.0",
    "host": "127.0.0.1:8000",
    "basePath": "/api/",
    "info": {
        "version": "1.0.0",
        "title": "Simple Covid 19 DashBoard API",
        "description": "A simple API to learn how to write OpenAPI Specification"
    },
    "schemes": [
        "http",
        "https"
    ],
    "consumes": [
        "application/json"
    ],
    "produces": [
        "application/json"
    ],
    "securityDefinitions": {
        "oauth": {
            "tokenUrl": "http://auth.ibtspl.com/oauth2/",
            "flow": "password",
            "scopes": {
                "read": "read users",
                "write": "create users",
                "update": "update users",
                "delete": "delete users",
                "superuser": "super user permission"
            },
            "type": "oauth2"
        }
    },
    "definitions": {
        "UserDetails": {
            "type": "object",
            "properties": {
                "user_name": {
                    "type": "string"
                },
                "password": {
                    "type": "string"
                },
                "confirm_password": {
                    "type": "string"
                }
            },
            "required": [
                "user_name",
                "password",
                "confirm_password"
            ]
        },
        "SignInDetails": {
            "type": "object",
            "properties": {
                "user_name": {
                    "type": "string"
                },
                "password": {
                    "type": "string"
                }
            }
        },
        "DayTotalCountsUpdates": {
            "type": "object",
            "properties": {
                "date_of_day": {
                    "type": "string"
                },
                "total_confirmed_cases": {
                    "type": "integer"
                },
                "total_deaths": {
                    "type": "integer"
                },
                "total_recovered_cases": {
                    "type": "integer"
                },
                "active_cases_on_the_day": {
                    "type": "integer"
                }
            },
            "required": [
                "date_of_day",
                "total_confirmed_cases",
                "total_deaths",
                "total_recovered_cases",
                "active_cases_on_the_day"
            ]
        },
        "StateTotalCountsUpdateOnADay": {
            "allOf": [{
                    "$ref": "#/definitions/DayTotalCountsUpdates"
                },
                {
                    "type": "object",
                    "properties": {
                        "state_name": {
                            "type": "string"
                        }
                    },
                    "required": [
                        "state_name"
                    ]
                }
            ]
        },
        "DistrictTotalCountsUpdateOnADay": {
            "allOf": [{
                    "$ref": "#/definitions/DayTotalCountsUpdates"
                },
                {
                    "type": "object",
                    "properties": {
                        "district_name": {
                            "type": "string"
                        }
                    },
                    "required": [
                        "district_name"
                    ]
                }
            ]
        },
        "MandalTotalCountsUpdateOnADay": {
            "allOf": [{
                    "$ref": "#/definitions/DayTotalCountsUpdates"
                },
                {
                    "type": "object",
                    "properties": {
                        "mandal_name": {
                            "type": "string"
                        }
                    },
                    "required": [
                        "mandal_name"
                    ]
                }
            ]
        },
        "AnalysisDetails": {
            "type": "object",
            "properties": {
                "date_of_day": {
                    "type": "string"
                },
                "total_confirmed_cases": {
                    "type": "integer"
                }
            }
        },
        "MandalAnalysis": {
            "type": "object",
            "properties": {
                "mandal_name": {
                    "type": "string"
                },
                "mandal_updates_day_wise": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/AnalysisDetails"
                    }
                }
            },
            "required": [
                "mandal_updates_day_wise"
            ]
        },
        "DistrictAnalysis": {
            "type": "object",
            "properties": {
                "district_name": {
                    "type": "string"
                },
                "ditrict_updates_day_wise": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/AnalysisDetails"
                    }
                }
            },
            "required": [
                "ditrict_updates_day_wise"
            ]
        },
        "StateDailyCountsUpdates": {
            "type": "object",
            "properties": {
                "state_name": {
                    "type": "string"
                },
                "state_updates_day_wise": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/StateTotalCountsUpdateOnADay"
                    }
                }
            },
            "required": [
                "state_updates_day_wise"
            ]
        },
        "DistrictDailyCountsUpdates": {
            "type": "object",
            "properties": {
                "state_name": {
                    "type": "string"
                },
                "ditrict_updates_day_wise": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/DistrictTotalCountsUpdateOnADay"
                    }
                }
            },
            "required": [
                "ditrict_updates_day_wise"
            ]
        },
        "MandalWiseDayCountsUpdates": {
            "properties": {
                "district_name": {
                    "type": "string"
                },
                "list_of_district_mandals_cumulative_updates": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/MandalTotalCountsUpdateOnADay"
                    }
                }
            },
            "required": [
                "list_of_district_mandals_cumulative_updates"
            ]
        },
        "MandalWiseDailyCountsUpdates": {
            "properties": {
                "district_name": {
                    "type": "string"
                },
                "list_of_district_mandals_cumulative_updates_day_wise": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/MandalTotalCountsUpdateOnADay"
                    }
                }
            },
            "required": [
                "list_of_district_mandals_cumulative_updates_day_wise"
            ]
        },
        "DistrictCumulativeStatsticsDay": {
            "allOf": [{
                    "$ref": "#/definitions/DistrictTotalCountsUpdateOnADay"
                },
                {
                    "$ref": "#/definitions/MandalWiseDayCountsUpdates"
                }
            ]
        },
        "StateCumulativeStatsticsDay": {
            "allOf": [{
                    "$ref": "#/definitions/StateTotalCountsUpdateOnADay"
                },
                {
                    "$ref": "#/definitions/DistrictWiseDayCountsUpdates"
                }
            ]
        },
        "DistrictWiseDailyCountsUpdates": {
            "properties": {
                "state_name": {
                    "type": "string"
                },
                "list_of_state_districts_cumulative_updates_day_wise": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/DistrictTotalCountsUpdateOnADay"
                    }
                }
            },
            "required": [
                "list_of_state_districts_cumulative_updates_day_wise"
            ]
        },
        "DistrictWiseDayCountsUpdates": {
            "properties": {
                "state_name": {
                    "type": "string"
                },
                "list_of_state_districts_cumulative_updates": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/DistrictTotalCountsUpdateOnADay"
                    }
                }
            },
            "required": [
                "list_of_state_districts_cumulative_updates"
            ]
        },
        "DistrictCumulativeStatsticsDayWise": {
            "allOf": [{
                    "$ref": "#/definitions/DistrictDailyCountsUpdates"
                },
                {
                    "$ref": "#/definitions/MandalWiseDailyCountsUpdates"
                }
            ]
        },
        "StateCumulativeStatsticsDayWise": {
            "allOf": [{
                    "$ref": "#/definitions/StateDailyCountsUpdates"
                },
                {
                    "$ref": "#/definitions/DistrictWiseDailyCountsUpdates"
                }
            ]
        }
    },
    "parameters": {
        "SignUpUser": {
            "name": "user_details",
            "in": "body",
            "required": true,
            "description": "The details has needed to sign up.",
            "schema": {
                "$ref": "#/definitions/UserDetails"
            }
        },
        "SignInUser": {
            "name": "user_details",
            "in": "body",
            "required": true,
            "description": "The details has needed to sign up.",
            "schema": {
                "$ref": "#/definitions/SignInDetails"
            }
        },
        "StateParameters": {
            "name": "user_details",
            "in": "body",
            "required": true,
            "description": "enter the data of a day to insert into state details.",
            "schema": {
                "type": "object",
                "properties": {
                    "date_of_day": {
                        "type": "string"
                    },
                    "confirmed_cases": {
                        "type": "integer"
                    },
                    "deaths": {
                        "type": "integer"
                    },
                    "recovered_cases": {
                        "type": "integer"
                    }
                }
            }
        },
        "StateIdPathParameter": {
            "name": "state_id",
            "in": "path",
            "required": true,
            "description": "The selected state id",
            "type": "string"
        },
        "MandalIdPathParameter": {
            "name": "mandal_id",
            "in": "path",
            "required": true,
            "description": "The selected mandal id",
            "type": "string"
        },
        "DistrictIdPathParameter": {
            "name": "district_id",
            "in": "path",
            "required": true,
            "description": "The selected district Id",
            "type": "string"
        },
        "DateParameter": {
            "name": "date",
            "in": "body",
            "required": true,
            "description": "The selected date",
            "schema": {
                "type": "object",
                "properties": {
                    "date": {
                        "type": "string"
                    }
                },
                "required": [
                    "date"
                ]
            }
        }
    },
    "responses": {},
    "paths": {
        "/sign_up/v1/": {
            "post": {
                "security": [{
                    "oauth": [
                        "write",
                        "read"
                    ]
                }],
                "operationId": "sign_up_user",
                "parameters": [{
                    "$ref": "#/parameters/SignUpUser"
                }],
                "responses": {
                    "201": {
                        "description": "Registered Successfully!!",
                        "schema": {
                            "type": "object",
                            "properties": {
                                "user_name": {
                                    "type": "string"
                                },
                                "password": {
                                    "type": "string"
                                }
                            },
                            "required": [
                                "user_name",
                                "password"
                            ]
                        }
                    },
                    "409": {
                        "description": "User name already exists"
                    },
                    "400": {
                        "description": "Enter valid details"
                    }
                }
            }
        },
        "/sign_in/v1/": {
            "get": {
                "security": [{
                    "oauth": [
                        "read",
                        "write"
                    ]
                }],
                "operationId": "sign_in_user",
                "parameters": [{
                    "$ref": "#/parameters/SignInUser"
                }],
                "responses": {
                    "200": {
                        "description": "Successful",
                        "schema": {
                            "type": "object",
                            "properties": {
                                "user_id": {
                                    "type": "integer"
                                },
                                "access_token": {
                                    "type": "string"
                                },
                                "refresh_token": {
                                    "type": "string"
                                },
                                "expires_in": {
                                    "type": "string"
                                }
                            },
                            "required": [
                                "access_token"
                            ]
                        }
                    },
                    "409": {
                        "description": "User name already exists"
                    },
                    "401": {
                        "description": "Incorrect password"
                    },
                    "400": {
                        "description": "Invalid username and password"
                    }
                }
            }
        },
        "/state/{state_id}/report/cumulative/total/v1/": {
            "get": {
                "security": [{
                    "oauth": [
                        "read",
                        "write"
                    ]
                }],
                "operationId": "get_state_cumulative_report",
                "parameters": [{
                        "$ref": "#/parameters/StateIdPathParameter"
                    },
                    {
                        "$ref": "#/parameters/DateParameter"
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Successful",
                        "schema": {
                            "$ref": "#/definitions/StateCumulativeStatsticsDay"
                        }
                    },
                    "400": {
                        "description": "Invalid state_id"
                    }
                }
            }
        },
        "/state/{state_id}/report/cumulative/day_wise/v1/": {
            "get": {
                "security": [{
                    "oauth": [
                        "read",
                        "write"
                    ]
                }],
                "operationId": "get_state_cumulative_report_day_wise",
                "parameters": [{
                        "$ref": "#/parameters/StateIdPathParameter"
                    },
                    {
                        "name": "start_date",
                        "in": "header",
                        "required": true,
                        "description": "Enter date to get start from that date",
                        "type": "integer"
                    },
                    {
                        "name": "end_date",
                        "in": "header",
                        "required": true,
                        "description": "Enter end date upto where you need",
                        "type": "integer"
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Successful",
                        "schema": {
                            "$ref": "#/definitions/StateCumulativeStatsticsDayWise"
                        }
                    },
                    "400": {
                        "description": "Invalid state_id"
                    }
                }
            }
        },
        "/state/{state_id}/create_state_record/v1/": {
            "post": {
                "security": [{
                    "oauth": [
                        "write",
                        "read"
                    ]
                }],
                "operationId": "Add a day record to state details",
                "parameters": [{
                        "$ref": "#/parameters/StateIdPathParameter"
                    },
                    {
                        "$ref": "#/parameters/StateParameters"
                    }
                ],
                "responses": {
                    "201": {
                        "description": "Added Successfully!!"
                    },
                    "409": {
                        "description": "data_already already exists"
                    },
                    "400": {
                        "description": "Enter valid details"
                    }
                }
            }
        },
        "/district/{district_id}/report/cumulative/total/v1/": {
            "get": {
                "security": [{
                    "oauth": [
                        "read",
                        "write"
                    ]
                }],
                "operationId": "get_district_cumulative_report",
                "parameters": [{
                        "$ref": "#/parameters/DistrictIdPathParameter"
                    },
                    {
                        "$ref": "#/parameters/DateParameter"
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Successful",
                        "schema": {
                            "$ref": "#/definitions/DistrictCumulativeStatsticsDay"
                        }
                    },
                    "400": {
                        "description": "Invalid district_id"
                    }
                }
            }
        },
        "/state/{district_id}/report/district_analysis/v1/": {
            "get": {
                "security": [{
                    "oauth": [
                        "read",
                        "write"
                    ]
                }],
                "operationId": "get_district_analysis_report",
                "parameters": [{
                        "$ref": "#/parameters/DistrictIdPathParameter"
                    },
                    {
                        "$ref": "#/parameters/DateParameter"
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Successful",
                        "schema": {
                            "$ref": "#/definitions/DistrictAnalysis"
                        }
                    },
                    "400": {
                        "description": "Invalid district_id"
                    }
                }
            }
        },
        "/district/{mandal_id}/report/mandal_analysis/v1/": {
            "get": {
                "security": [{
                    "oauth": [
                        "read",
                        "write"
                    ]
                }],
                "operationId": "get_mandal_analysis_report",
                "parameters": [{
                        "$ref": "#/parameters/MandalIdPathParameter"
                    },
                    {
                        "$ref": "#/parameters/DateParameter"
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Successful",
                        "schema": {
                            "$ref": "#/definitions/MandalAnalysis"
                        }
                    },
                    "400": {
                        "description": "Invalid district_id"
                    }
                }
            }
        },
        "/district/{district_id}/report/cumulative/day_wise/v1/": {
            "get": {
                "security": [{
                    "oauth": [
                        "read",
                        "write"
                    ]
                }],
                "operationId": "get_district_cumulative_report_day_wise",
                "parameters": [{
                        "$ref": "#/parameters/DistrictIdPathParameter"
                    },
                    {
                        "name": "start_date",
                        "in": "header",
                        "required": true,
                        "description": "Enter date to get start from that date",
                        "type": "integer"
                    },
                    {
                        "name": "end_date",
                        "in": "header",
                        "required": true,
                        "description": "Enter end date upto where you need",
                        "type": "integer"
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Successful",
                        "schema": {
                            "$ref": "#/definitions/DistrictCumulativeStatsticsDayWise"
                        }
                    },
                    "400": {
                        "description": "Invalid district_id"
                    }
                }
            }
        }
    }
}
